queries.query:
MATCH (o:esco__Occupation)

return { ContentType: 'Occupation', TitlePart:{Title:o.skos__prefLabel}
,GraphSyncPart:{Text:o.uri},
Occupation:
{AlternativeLabels:{ContentItemIds:[(o)-[:ncs__hasAltLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("OccupationLabel", "'+l.skos__prefLabel+'")]']},PreferredLabel:{ContentItemIds:[(o)-[:ncs__hasPrefLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("OccupationLabel", "'+l.skos__prefLabel+'")]']}
}
}
order by o.skos__prefLabel
skip 1500 limit 300
