queries.query:
MATCH (o:esco__Occupation)

return { ContentType: 'Occupation', TitlePart:{Title:o.skos__prefLabel}
,GraphSyncPart:{Text:o.uri},
Occupation:
{AlternativeLabels:{ContentItemIds:[(o)-[:hasAltLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("OccupationLabel", "'+l.skos__prefLabel+'")]']},PreferredLabel:{ContentItemIds:[(o)-[:hasPrefLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("OccupationLabel", "'+l.skos__prefLabel+'")]']},EssentialSkills:{ContentItemIds:[(o)-[:esco__relatedEssentialSkill]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("Skill", "'+l.skos__prefLabel+'")]']},OptionalSkills:{ContentItemIds:[(o)-[:esco__relatedOptionalSkill]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("Skill", "'+l.skos__prefLabel+'")]']}
}
}
order by o.skos__prefLabel
skip 2000 limit 400
