Widget__CardContainer.Content:
{% assign isPreview = Request.Headers.IsPreview | raw %}

<div class="card-container">
    {% assign contentItems = Model.ContentItem.Content.CardContainer.Cards.ContentItemIds %}
    {% for contentItem in contentItems %}
        {% if isPreview == "True" %}
            {% assign id = "ContentItemId:" | append: contentItem %}               
            {{ Content.Latest[id] | shape_build_display:  Content.Latest[id].ContentType | shape_render  }}
        {% else %}  
            {% assign contentData = Content.ContentItemId[contentItem]  %}
            {{ contentData | shape_build_display: contentData.ContentType | shape_render }}
        {% endif %}
    {% endfor %}
</div>

Content_ProductCard.Content:
{% assign card = Model.ContentItem.Content.ProductCard %}
{% assign dysacIconPath = "checkbox.svg" %}
{% assign facIconPath = "magnifying-glass.svg" %}
{% assign ecIconPath = "compass.svg" %}
{% assign templateId = card.Template.ContentItemIds.first %}
{% assign template = Content.ContentItemId[templateId].DisplayText%}

{% if template == "Find a course"%}
 {%assign headerStyle = "fac-product-card-header"%}
 {%assign iconPath = facIconPath %}
 {%assign borderStyle = "fac-product-card"%}
{%endif%}
{% if template == "Skills assessment"%}
 {%assign headerStyle = "dysac-product-card-header"%}
 {%assign iconPath = dysacIconPath %}
 {%assign borderStyle = "dysac-product-card"%}
{%endif%}
{% if template == "Explore careers"%}
 {%assign headerStyle = "ec-product-card-header"%}
 {%assign iconPath = ecIconPath %}
 {%assign borderStyle = "ec-product-card"%}
{%endif%}

<div class="ncs-card-product {{borderStyle}} product-card-default-size">
    <div class="card-header {{headerStyle}}">
        <h3>
            <a href="{{ card.LinkTitle.Url }}"> {{ card.LinkTitle.Text }} </a>
        </h3>
        <img src="{{iconPath | asset_url}}"/>
    </div>
    <div class="content">
        {{ card.Description.Html | raw }}
    </div>
</div>
