queries.query:
MATCH (o:esco__Occupation)
[token:whereClause]
return { ContentType: 'Occupation', TitlePart:{Title:o.skos__prefLabel}
,GraphSyncPart:{Text:o.uri},
Occupation:
{AlternativeLabels:{ContentItemIds:[(o)-[:hasAltLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("OccupationLabel", "'+l.skos__prefLabel+'")]']},PreferredLabel:{ContentItemIds:[(o)-[:hasPrefLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("OccupationLabel", "'+l.skos__prefLabel+'")]']}
}
}
order by o.skos__prefLabel
skip [token:skip] limit [token:limit]
