queries.query:
MATCH [token:occupationMatch](sk:esco__Skill)-[r:dct__description]-(s {esco__language: "en"})[token:whereClause] with distinct sk, s
return { ContentType: 'Skill', TitlePart:{Title:sk.skos__prefLabel}
,GraphSyncPart:{Text:sk.uri},
Skill:{Description:{Html:s.esco__nodeLiteral},AlternativeLabels:{ContentItemIds:[(sk)-[:hasAltLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("SkillLabel", "'+l.skos__prefLabel+'")]']},PreferredLabel:{ContentItemIds:[(sk)-[:hasPrefLabel]->(l) | '[c#: await Content.GetContentItemIdByDisplayText("SkillLabel", "'+l.skos__prefLabel+'")]']}}
}
order by sk.skos__prefLabel
skip [token:skip] limit [token:limit]
